pragma solidity ^0.8.9;

// third party
import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {IERC725Y} from "@erc725/smart-contracts/contracts/interfaces/IERC725Y.sol";

// local
import {SocialNetworkPostType} from "./SocialNetworkPostType.sol";

/**
 * @title Social Network Post Interface
 * @author Dennis Tuszynski
 * @dev Interface which describes all the function declarations for a SocialNetworkPost contract (LSP8 Token instance).
 */
interface ISocialNetworkPost is IERC165, IERC725Y {
    /**
     * @notice Returns the author of the post
     */
    function author() external view returns (address);

    /**
     * @notice Returns the timestamp of the post
     */
    function timestamp() external view returns (uint);

    /**
     * @notice Returns the type of the post
     */
    function postType() external view returns (SocialNetworkPostType);

    /**
     * @notice Returns the referencedPost of the post
     */
    function referencedPost() external view returns (address);
    
    /**
     * @notice Checks if the given user is tagged in the post
     * @param _user address of the user to check
     */
    function isUserTagged(address _user) external view returns (bool);


    /**
     * @notice Checks if the given user liked the post
     * @param _user address of the user to check
     */
    function isLikedBy(address _user) external view returns (bool);

    /**
     * @notice Adds user like to post
     * @param _user address of the user to be added to the enumerable set 'likes'
     */
    function addLike(address _user) external;

    /**
     * @notice Removes user like from post
     * @param _user address of the user to be removed from the enumerable set 'likes'
     */
    function removeLike(address _user) external;

    /**
     * @notice Adds comment to post
     * @param _comment address of the post to be added to the enumerable set 'comments'
     */
    function addComment(address _comment) external;

    /**
     * @notice Adds share to post
     * @param _share address of the post to be added to the enumerable set 'shares'
     */
    function addShare(address _share) external;
}
","keccak256":"0x0ab4d7049fc8bcd5593d091e078432e008883e808ca16b6b62103637b1088611","license":"Apache-2.0"},"contracts/ISocialNetworkProfileData.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.9;

// third party
import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {IERC725Y} from "@erc725/smart-contracts/contracts/interfaces/IERC725Y.sol";

/**
 * @title Social Network Profile Data Interface
 * @author Dennis Tuszynski
 * @dev Interface which describes the social network profile data that is 'assigned' to a universal profile.
 * Storing the data within a separate contract instead of in the universal profile itself has the following advantages:
 * - Prevention of data manipulation/corruption with manual 'setData' invocations by the universal profile owner, since only the managing SocialNetwork contract has write access
 * - Reduction of gas cost because of direct data manipulation by the owning SocialMedia contract instead of going through the KeyManager (especially 'verifyPermissions')
 * - Easier Onboarding of users since only a CALL permission with the SocialNetwork's contract address (ALLOWED ADDRESS) must be set on the universal profile's key manager
 * - Existence of a SocialNetworkProfileData contract instance is comparable with a successful registration
 * - (No validation modifiers necessary since validation is handled in managing SocialNetwork contract)
 */
interface ISocialNetworkProfileData is IERC165, IERC725Y {
       /**
     * @notice Returns the linked user profile of the profile data
     */
    function user() external view returns (address);

    /**
     * @notice Returns the timestamp of the post
     */
    function timestamp() external view returns (uint);

    /**
     * @notice Checks if the the social profile is tagged in the given post
     * @param _post address of the post to check
     */
    function isTaggedIn(address _post) external view returns (bool);

    /**
     * @notice Adds tag to social profile
     * @param _post address of the post to be added to the enumerable set 'tags'
     */
    function addTag(address _post) external;

    /**
     * @notice Checks if the the social profile is author of the given post
     * @param _post address of the post to check
     */
    function isAuthorOf(address _post) external view returns (bool);

    /**
     * @notice Adds post to social profile
     * @param _post address of the post to be added to the enumerable set 'posts'
     */
    function addPost(address _post) external;

    /**
     * @notice Checks if the given post is liked by the social profile
     * @param _post address of the post to check
     */
    function hasLiked(address _post) external view returns (bool);

    /**
     * @notice Adds post like to social profile (action: post liked by user)
     * @param _post address of the post to be added to the enumerable set 'likes'
     */
    function addLike(address _post) external;

    /**
     * @notice Removes post like from social profile (action: post unliked by user)
     * @param _post address of the post to be removed from the enumerable set 'likes'
     */
    function removeLike(address _post) external;

    /**
     * @notice Checks if the given user subscribed the social profile
     * @param _user address of the user to check
     */
    function isSubscribedBy(address _user) external view returns (bool);

    /**
     * @notice Adds subscriber to social profile
     * @param _user address of the user to be added to the enumerable set 'subscribers'
     */
    function addSubscriber(address _user) external;

    /**
     * @notice Removes subscriber from social profile
     * @param _user address of the user to be removed from the enumerable set 'subscribers'
     */
    function removeSubscriber(address _user) external;

    /**
     * @notice Checks if the the social profile subscribed the given user
     * @param _user address of the user to check
     */
    function isSubscriberOf(address _user) external view returns (bool);

    /**
     * @notice Adds subscription to social profile
     * @param _user address of the user to be added to the enumerable set 'subscriptions'
     */
    function addSubscription(address _user) external;

    /**
     * @notice Removes subscription from social profile
     * @param _user address of the user to be removed from the enumerable set 'subscriptions'
     */
    function removeSubscription(address _user) external;
}
","keccak256":"0x13cdc3ae6cb4b9c7cf166635fe4031543e585387570cbd74c7be9ea20de3cba2","license":"Apache-2.0"},"contracts/LSP2KeyUtil.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.9;

/**
 * @title Custom LSP2 utils library: https://github.com/lukso-network/LIPs/blob/main/LSPs/LSP-2-ERC725YJSONSchema.md
 * @author Dennis Tuszynski
 * @dev Implementation of a library which adds some custom helper functions for calculating LSP2 conforming key names.
 */
library LSP2KeyUtil {
     /**
     * @notice Calculates LSP2 conforming key name for mapping type
     * @param _mapKeyNamePrefix the LSP2 conforming mapping key name prefix
     * @param _address the address to be used as the key name suffix (mapped address)
     */
    function getMappedAddressKeyName(bytes12 _mapKeyNamePrefix, address _address) external pure returns (bytes32) {
        return bytes32(bytes.concat(_mapKeyNamePrefix, bytes20(_address)));
    }
}","keccak256":"0xac31e994f4836180eebadb656d8f8dd1168d89bb0f21a6d442fb7ef7e22e4940","license":"Apache-2.0"},"contracts/SocialNetwork.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.9;

// third party
import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {ERC165} from "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import {IERC725Y} from "@erc725/smart-contracts/contracts/interfaces/IERC725Y.sol";
import {_INTERFACEID_ERC725Y, _INTERFACEID_ERC725X} from "@erc725/smart-contracts/contracts/constants.sol";
import {ERC725YCore} from "@erc725/smart-contracts/contracts/ERC725YCore.sol";
import {ERC725Y} from "@erc725/smart-contracts/contracts/ERC725Y.sol";
import {_LSP4_METADATA_KEY} from "@lukso/lsp-smart-contracts/contracts/LSP4DigitalAssetMetadata/LSP4Constants.sol";
import {LSP8IdentifiableDigitalAsset} from "@lukso/lsp-smart-contracts/contracts/LSP8IdentifiableDigitalAsset/LSP8IdentifiableDigitalAsset.sol";
import {OwnableUnset} from "@erc725/smart-contracts/contracts/custom/OwnableUnset.sol";

// local
import {ISocialNetwork} from "./ISocialNetwork.sol";
import {SocialNetworkProfileDataFactory} from "./SocialNetworkProfileDataFactory.sol";
import {ISocialNetworkProfileData} from "./ISocialNetworkProfileData.sol";
import {SocialNetworkPostFactory} from "./SocialNetworkPostFactory.sol";
import {ISocialNetworkPost} from "./ISocialNetworkPost.sol";
import {SocialNetworkPostType} from "./SocialNetworkPostType.sol";

// constants
import "./SocialNetworkConstants.sol";

/**
 * @title Social Network Implementation
 * @author Dennis Tuszynski
 * @dev Contract module represents a social network.
 */
contract SocialNetwork is
    ISocialNetwork,
    LSP8IdentifiableDigitalAsset("Social Network Post", "SNP", msg.sender)
{
    mapping(address => address) public registeredUsers; // mapping from universal profile to social network profile data
    uint public registeredUserCount = 0; // incremented with each registration

    constructor(bytes memory _LSP4MetadataJSONURL) {
        // set token id type to be address (tokens => instances of SocialNetworkPost contract)
        setData(
            _LSP8_TOKEN_ID_TYPE,
            bytes(abi.encode(_LSP8_TOKEN_ID_TYPE_ADDRESS))
        );
        setData(_LSP4_METADATA_KEY, _LSP4MetadataJSONURL);
    }

    /**
     * @notice Validates that the given address is a registered user
     * @param _user the user address to be checked
     */
    modifier onlyRegisteredUser(address _user) {
        require(
            registeredUsers[_user] != address(0),
            "User address is not registered"
        );
        _;
    }

    /**
     * @notice Validates that the given address is a valid post (instance of SocialNetworkPost contract)
     * @param _post the post address to be checked
     */
    modifier onlyValidPost(address _post) {
        require(
            IERC165(_post).supportsInterface(_INTERFACEID_SOCIAL_NETWORK_POST),
            "Post address is not pointing to a valid post (SocialNetworkPost interface not available)"
        );
        require(
            OwnableUnset(_post).owner() == address(this),
            "Post address is not owned by current SocialNetwork contract instance"
        );
        _;
    }

    /**
     * @notice Validates that the user has liked the post
     * @param _user the address of the user
     * @param _targetPost the address of the post
     */
    modifier onlyLikedPost(address _user, address _targetPost) {
        require(
            ISocialNetworkPost(_targetPost).isLikedBy(_user),
            "User did not like the post yet"
        );
        _;
    }

    /**
     * @notice Validates that the user has not liked the post
     * @param _user the address of the user
     * @param _targetPost the address of the post
     */
    modifier onlyNotLikedPost(address _user, address _targetPost) {
        require(
            !ISocialNetworkPost(_targetPost).isLikedBy(_user),
            "User has already liked the post"
        );
        _;
    }

    /**
     * @notice Validates that the user has subscribed the target user
     * @param _user the address of the user
     * @param _targetUser the address of the target user
     */
    modifier onlySubscribedUser(address _user, address _targetUser) {
        require(_user != _targetUser, "Users must be different");
        require(
            ISocialNetworkProfileData(registeredUsers[_user]).isSubscriberOf(
                _targetUser
            ),
            "User is not a subscriber yet"
        );
        _;
    }

    /**
     * @notice Validates that the user has not subscribed the target user
     * @param _user the address of the user
     * @param _targetUser the address of the target user
     */
    modifier onlyNotSubscribedUser(address _user, address _targetUser) {
        require(_user != _targetUser, "Users must be different");
        require(
            !ISocialNetworkProfileData(registeredUsers[_user]).isSubscriberOf(
                _targetUser
            ),
            "User is already a subscriber"
        );
        _;
    }

    /**
     * @notice Validates that the user is the author of the post
     * @param _user the address of the user
     * @param _targetPost the address of the post
     */
    modifier onlyPostAuthor(address _user, address _targetPost) {
        require(
            _user == ISocialNetworkPost(_targetPost).author(),
            "User must be the post author"
        );
        _;
    }

    /**
     * @notice Requires that the user is not the author of the post
     * @param _user the address of the user
     * @param _targetPost the address of the post
     */
    modifier onlyNotPostAuthor(address _user, address _targetPost) {
        require(
            _user != ISocialNetworkPost(_targetPost).author(),
            "User must not be the post author"
        );
        _;
    }

    /**
     * @inheritdoc ISocialNetwork
     * @dev Creates a new instance of the SocialNetworkProfileData contract and links it to the sender address.
     * Fails if the sender address is not a universal profile or if the sender address is already registered.
     */
    function register() external returns (address) {
        require(
            registeredUsers[msg.sender] == address(0),
            "User address is already registered"
        );
        require(
            msg.sender.code.length > 0,
            "User address is an EOA - Only smart contract based accounts are supported"
        );
        // TODO: Find a way to check supportsInterface LSP0ERC725Account instead
        // require(
        //     IERC165(msg.sender).supportsInterface(_INTERFACEID_ERC725Y) &&
        //         IERC165(msg.sender).supportsInterface(_INTERFACEID_ERC725X),
        //     "User address is not an universal profile (ERC725X and/or ERC725Y interfaces not available)"
        // );

        ++registeredUserCount;
        registeredUsers[msg.sender] = SocialNetworkProfileDataFactory
            .createProfileData(address(this), msg.sender);
        emit UserRegistered(
            msg.sender,
            registeredUsers[msg.sender],
            registeredUserCount,
            block.timestamp
        );

        return registeredUsers[msg.sender];
    }

    /**
     * @inheritdoc ISocialNetwork
     */
    function likePost(address _post)
        external
        onlyRegisteredUser(msg.sender)
        onlyValidPost(_post)
        onlyNotPostAuthor(msg.sender, _post)
        onlyNotLikedPost(msg.sender, _post)
    {
        ISocialNetworkPost(_post).addLike(msg.sender);
        ISocialNetworkProfileData(registeredUsers[msg.sender]).addLike(_post);
        emit UserLikedPost(msg.sender, _post, block.timestamp);
    }

    /**
     * @inheritdoc ISocialNetwork
     */
    function unlikePost(address _post)
        external
        onlyRegisteredUser(msg.sender)
        onlyValidPost(_post)
        onlyNotPostAuthor(msg.sender, _post)
        onlyLikedPost(msg.sender, _post)
    {
        ISocialNetworkPost(_post).removeLike(msg.sender);
        ISocialNetworkProfileData(registeredUsers[msg.sender]).removeLike(
            _post
        );
        emit UserUnlikedPost(msg.sender, _post, block.timestamp);
    }

    /**
     * @inheritdoc ISocialNetwork
     */
    function subscribeUser(address _user)
        external
        onlyRegisteredUser(msg.sender)
        onlyRegisteredUser(_user)
        onlyNotSubscribedUser(msg.sender, _user)
    {
        ISocialNetworkProfileData(registeredUsers[msg.sender]).addSubscription(
            _user
        );
        ISocialNetworkProfileData(registeredUsers[_user]).addSubscriber(
            msg.sender
        );
        emit UserSubscribedUser(msg.sender, _user, block.timestamp);
    }

    /**
     * @inheritdoc ISocialNetwork
     */
    function unsubscribeUser(address _user)
        external
        onlyRegisteredUser(msg.sender)
        onlyRegisteredUser(_user)
        onlySubscribedUser(msg.sender, _user)
    {
        ISocialNetworkProfileData(registeredUsers[msg.sender])
            .removeSubscription(_user);
        ISocialNetworkProfileData(registeredUsers[_user]).removeSubscriber(
            msg.sender
        );
        emit UserUnsubscribedUser(msg.sender, _user, block.timestamp);
    }

    /**
     * @inheritdoc ISocialNetwork
     */
    function createPost(bytes calldata _data, address[] calldata _taggedUsers)
        external
        onlyRegisteredUser(msg.sender)
        returns (address)
    {
        address post = SocialNetworkPostFactory.createPost(
            address(this),
            msg.sender,
            _data,
            _taggedUsers
        );

        _mint(
            msg.sender,
            bytes32(bytes.concat(bytes12(0), bytes20(post))),
            false,
            ""
        );

        ISocialNetworkProfileData(registeredUsers[msg.sender]).addPost(post);

        emit UserCreatedPost(SocialNetworkPostType.STANDALONE, msg.sender, address(0), post, block.timestamp);
        handleUserTags(post, _taggedUsers);

        return post;
    }

    /**
     * @inheritdoc ISocialNetwork
     */
    function commentPost(
        bytes calldata _data,
        address[] calldata _taggedUsers,
        address _targetPost
    )
        external
        onlyRegisteredUser(msg.sender)
        onlyValidPost(_targetPost)
        returns (address)
    {
        address post = SocialNetworkPostFactory.createCommentPost(
            address(this),
            msg.sender,
            _data,
            _taggedUsers,
            _targetPost
        );

        _mint(
            msg.sender,
            bytes32(bytes.concat(bytes12(0), bytes20(post))),
            false,
            ""
        );

        ISocialNetworkPost(_targetPost).addComment(post);
        ISocialNetworkProfileData(registeredUsers[msg.sender]).addPost(post);

        emit UserCreatedPost(SocialNetworkPostType.COMMENT, msg.sender, _targetPost, post, block.timestamp);
        handleUserTags(post, _taggedUsers);

        return post;
    }

    /**
     * @inheritdoc ISocialNetwork
     */
    function sharePost(
        bytes calldata _data,
        address[] calldata _taggedUsers,
        address _targetPost
    )
        external
        onlyRegisteredUser(msg.sender)
        onlyValidPost(_targetPost)
        returns (address)
    {
        address post = SocialNetworkPostFactory.createSharePost(
            address(this),
            msg.sender,
            _data,
            _taggedUsers,
            _targetPost
        );

        _mint(
            msg.sender,
            bytes32(bytes.concat(bytes12(0), bytes20(post))),
            false,
            ""
        );

        ISocialNetworkPost(_targetPost).addShare(post);
        ISocialNetworkProfileData(registeredUsers[msg.sender]).addPost(post);

        emit UserCreatedPost(SocialNetworkPostType.SHARE, msg.sender, _targetPost, post, block.timestamp);
        handleUserTags(post, _taggedUsers);

        return post;
    }

    /**
     * @notice Emits UserTaggedUserInPost events and adds the given post to the according enumerable set of each user's social profile data contract instance.
     * @param _post The post in which the users were tagged
     * @param _taggedUsers The array of the users who were tagged
     */
    function handleUserTags(address _post, address[] calldata _taggedUsers)
        internal
    {
        uint taggedUsersLength = _taggedUsers.length;
        for (uint i = 0; i < taggedUsersLength; ++i) {
            handleUserTag(_post, _taggedUsers[i]);
        }
    }

    /**
     * @notice Emits UserTaggedUserInPost event and adds the given post to the according enumerable set of the user's social profile data contract instance.
     * @param _post The post in which the user was tagged
     * @param _taggedUser The address of the user who was tagged
     */
    function handleUserTag(address _post, address _taggedUser)
        internal
        onlyRegisteredUser(_taggedUser)
    {
        ISocialNetworkProfileData(registeredUsers[_taggedUser]).addTag(_post);

        emit UserTaggedUserInPost(
            msg.sender,
            _post,
            _taggedUser,
            block.timestamp
        );
    }

    /**
     * @inheritdoc ERC165
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(IERC165, LSP8IdentifiableDigitalAsset)
        returns (bool)
    {
        return
            interfaceId == _INTERFACEID_SOCIAL_NETWORK ||
            super.supportsInterface(interfaceId);
    }
}
","keccak256":"0xb8957667189093938c2adb3df64ce16d1ad2a92ef2b5bd4534eef045859a0ddc","license":"Apache-2.0"},"contracts/SocialNetworkConstants.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.9;

// >> ERC165 INTERFACE ID
bytes4 constant _INTERFACEID_SOCIAL_NETWORK = 0x52ae1080; // calculated according to https://eips.ethereum.org/EIPS/eip-165
bytes4 constant _INTERFACEID_SOCIAL_NETWORK_PROFILE_DATA = 0x9b0fef4c; // calculated according to https://eips.ethereum.org/EIPS/eip-165
bytes4 constant _INTERFACEID_SOCIAL_NETWORK_POST = 0x8ef076aa; // calculated according to https://eips.ethereum.org/EIPS/eip-165

// >> ERC725Y entries
uint constant _LSP8_TOKEN_ID_TYPE_ADDRESS = 1; // see https://github.com/lukso-network/LIPs/blob/main/LSPs/LSP-8-IdentifiableDigitalAsset.md
bytes32 constant _LSP8_TOKEN_ID_TYPE = 0x715f248956de7ce65e94d9d836bfead479f7e70d69b718d47bfe7b00e05b4fe4; // see https://github.com/lukso-network/LIPs/blob/main/LSPs/LSP-8-IdentifiableDigitalAsset.md
bytes32 constant _LSP8_TOKEN_ID_METADATA_MINTED_BY = 0xa0093ef0f6788cc87a372bbd12cf83ae7eeb2c85b87e43517ffd5b3978d356c9; // see https://github.com/lukso-network/LIPs/blob/main/LSPs/LSP-8-IdentifiableDigitalAsset.md
bytes32 constant _LSP8_TOKEN_ID_METADATA_TOKEN_ID = 0x51ea539c2c3a29af57cb4b60be9d43689bfa633dba8613743d1be7fb038d36c3; // see https://github.com/lukso-network/LIPs/blob/main/LSPs/LSP-8-IdentifiableDigitalAsset.md

// ERC725Y LSP8 Metadata Contract - Post Content Key
// {"name":"SNPostContent","key":"0x267100e4b0ea7c1e884866329768f0a9762ecf93ab52ada79174c067d41c4e15","keyType":"Singleton","valueType":"bytes","valueContent":"JSONURL"}
bytes32 constant _LSP8_TOKEN_ID_METADATA_SN_POST_CONTENT = 0x267100e4b0ea7c1e884866329768f0a9762ecf93ab52ada79174c067d41c4e15;

// ERC725Y EnumerableSet keys for Social Network Likes (map points to index in array, array contains universal profile addresses)
bytes32 constant _SN_LIKES_ARRAY_KEY = 0xd74f936d1543a0f1abf8190b00fe9837297a24c4b3d832915264e825bc3fc0c8; // keccak256('SNLikes[]')
bytes12 constant _SN_LIKES_MAP_KEY = 0x1e3d423e52a0cd35309e0000; // bytes10(keccak256('SNLikes')) + bytes2(0)

// ERC725Y EnumerableSet keys for Social Network Comments (map points to index in array, array contains social network post addresses)
bytes32 constant _SN_COMMENTS_ARRAY_KEY = 0xf2f114f920273bcb634939f1c6afd35a362fe32c9fe59f4a8c08d77fd74280dd; // keccak256('SNComments[]')
bytes12 constant _SN_COMMENTS_MAP_KEY = 0x9178381cc16012dc81a10000; // bytes10(keccak256('SNComments')) + bytes2(0)

// ERC725Y EnumerableSet keys for Social Network Shares (map points to index in array, array contains social network post addresses)
bytes32 constant _SN_SHARES_ARRAY_KEY = 0x72f27ddc359c9a543c3194ffa294ca7f32bed33c7d644a22949516d73c870ad8; // keccak256('SNShares[]')
bytes12 constant _SN_SHARES_MAP_KEY = 0x5fd6332ee7f2fac51c230000; // bytes10(keccak256('SNShares')) + bytes2(0)

// ERC725Y EnumerableSet keys for Social Network Subscriptions (map points to index in array, array contains universal profile addresses)
bytes32 constant _SN_SUBSCRIPTIONS_ARRAY_KEY = 0x5a1ba2b446b30525fe4638569ee418eac7ca8fcc0a03f2c4843dd04d27e8c1ac; // keccak256('SNSubscriptions[]')
bytes12 constant _SN_SUBSCRIPTIONS_MAP_KEY = 0xc108b10b147caa96224e0000; // bytes10(keccak256('SNSubscriptions')) + bytes2(0)

// ERC725Y EnumerableSet keys for Social Network Subscribers (map points to index in array, array contains universal profile addresses)
bytes32 constant _SN_SUBSCRIBERS_ARRAY_KEY = 0x8a82c15a8cea01b6a5d0db3b14e480dfe3197f0aa048e916d77b79abcc7a697c; // keccak256('SNSubscribers[]')
bytes12 constant _SN_SUBSCRIBERS_MAP_KEY = 0x90f039af6118e0ccbbd00000; // bytes10(keccak256('SNSubscribers')) + bytes2(0)

// ERC725Y EnumerableSet keys for Social Network User Tags (map points to index in array, array contains universal profile addresses)
bytes32 constant _SN_USER_TAGS_ARRAY_KEY = 0xafd89daf17abdaf5797bc0e7d4dbdc56a217d1b6e08b426b40b8f99b9a03fc6c; // keccak256('SNUserTags[]')
bytes12 constant _SN_USER_TAGS_MAP_KEY = 0x6737a26dc0027d9a47570000; // bytes10(keccak256('SNUserTags')) + bytes2(0)

// ERC725Y EnumerableSet keys for Social Network Posts (map points to index in array, array contains social network post addresses)
bytes32 constant _SN_POSTS_ARRAY_KEY = 0xe01ddb6ad4391dac642308cf420c56ac6a269002a7475dafeee62ea09be7a114; // keccak256('SNPosts[]')
bytes12 constant _SN_POSTS_MAP_KEY = 0x7e8492281728d34c260d0000; // bytes10(keccak256('SNPosts')) + bytes2(0)","keccak256":"0x75df6609f071b16afef55b6752f2e3aa3f7197b2cf1be2f4d5aa57b1b950ce1c","license":"Apache-2.0"},"contracts/SocialNetworkPost.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.9;

// third party
import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {ERC165} from "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import {IERC725Y} from "@erc725/smart-contracts/contracts/interfaces/IERC725Y.sol";
import {ERC725YCore} from "@erc725/smart-contracts/contracts/ERC725YCore.sol";
import {ERC725Y} from "@erc725/smart-contracts/contracts/ERC725Y.sol";

// local
import {ERC725YEnumerableSetUtil} from "./ERC725YEnumerableSetUtil.sol";
import {ISocialNetworkPost} from "./ISocialNetworkPost.sol";
import {SocialNetworkPostType} from "./SocialNetworkPostType.sol";

// constants
import "./SocialNetworkConstants.sol";

/**
 * @title Social Network Post Implementation
 * @author Dennis Tuszynski
 * @dev Contract module represents a social network post (LSP8 - Identifiable Asset).
 */
contract SocialNetworkPost is ISocialNetworkPost, ERC725YEnumerableSetUtil {
    uint public timestamp;
    address public author;
    SocialNetworkPostType public postType;
    address public referencedPost;

    /**
     * @notice Sets the contract variables and the ERC725Y JSONURL key that references the post content
     * @param _owner The owner address
     * @param _author address of the user who is the author of the post/share/comment
     * @param _postType post type enum value: STANDALONE (normal post), SHARE or COMMENT
     * @param _taggedUsers array of user addresses who are tagged in this post (max 3)
     * @param _data post content's json url formatted according to LSP2 JSONURL
     * @param _referencedPost if _postType is equal to STANDALONE then address(0) otherwise address of referenced post
     */
    constructor(
        address _owner,
        address _author,
        SocialNetworkPostType _postType,
        address[] memory _taggedUsers,
        bytes memory _data,
        address _referencedPost
    ) ERC725YEnumerableSetUtil(_owner) {
        require(
            _postType == SocialNetworkPostType.STANDALONE ||
                IERC165(_referencedPost).supportsInterface(
                    _INTERFACEID_SOCIAL_NETWORK_POST
                ),
            "Target post address must support the SOCIAL_NETWORK_POST interface (ERC165)"
        );
        require(_taggedUsers.length <= 3, "Amount of tagged users is greater than 3");
        if(_taggedUsers.length == 2){
            require(_taggedUsers[0] != _taggedUsers[1], "Tagged users must be different");
        }else if(_taggedUsers.length == 3){
            require(_taggedUsers[0] != _taggedUsers[1] && _taggedUsers[1] != _taggedUsers[2] && _taggedUsers[0] != _taggedUsers[2], "Tagged users must be different");
        }

        author = _author;
        postType = _postType;
        referencedPost = _postType == SocialNetworkPostType.STANDALONE
            ? address(0)
            : _referencedPost;
        timestamp = block.timestamp;

        // adds tagged users to ERC725Y based enumerable set
        uint taggedUserAmount = _taggedUsers.length;
        for (uint i = 0; i < taggedUserAmount; ++i) {
            addElementToEnumerableSet(
                _SN_USER_TAGS_MAP_KEY,
                _SN_USER_TAGS_ARRAY_KEY,
                _taggedUsers[i]
            );
        }

        setData(
            _LSP8_TOKEN_ID_METADATA_MINTED_BY,
            abi.encode(_author)
        );
        setData(
            _LSP8_TOKEN_ID_METADATA_TOKEN_ID,
            bytes.concat(bytes12(0), bytes20(address(this)))
        );
        setData(_LSP8_TOKEN_ID_METADATA_SN_POST_CONTENT, _data);
    }

    /**
     * @inheritdoc ISocialNetworkPost
     */
    function isUserTagged(address _user) external view returns (bool) {
        return isAddressInEnumerableSet(_SN_USER_TAGS_MAP_KEY, _user);
    }

    /**
     * @inheritdoc ISocialNetworkPost
     */
    function isLikedBy(address _user) external view returns (bool) {
        return isAddressInEnumerableSet(_SN_LIKES_MAP_KEY, _user);
    }

    /**
     * @inheritdoc ISocialNetworkPost
     */
    function addLike(address _user) external onlyOwner {
        addElementToEnumerableSet(
            _SN_LIKES_MAP_KEY,
            _SN_LIKES_ARRAY_KEY,
            _user
        );
    }

    /**
     * @inheritdoc ISocialNetworkPost
     */
    function removeLike(address _user) external onlyOwner {
        removeElementFromEnumerableSet(
            _SN_LIKES_MAP_KEY,
            _SN_LIKES_ARRAY_KEY,
            _user
        );
    }

    /**
     * @inheritdoc ISocialNetworkPost
     */
    function addComment(address _comment) external onlyOwner {
        addElementToEnumerableSet(
            _SN_COMMENTS_MAP_KEY,
            _SN_COMMENTS_ARRAY_KEY,
            _comment
        );
    }

    /**
     * @inheritdoc ISocialNetworkPost
     */
    function addShare(address _share) external onlyOwner {
        addElementToEnumerableSet(
            _SN_SHARES_MAP_KEY,
            _SN_SHARES_ARRAY_KEY,
            _share
        );
    }

    /**
     * @inheritdoc ERC165
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(IERC165, ERC725YCore)
        returns (bool)
    {
        return
            interfaceId == _INTERFACEID_SOCIAL_NETWORK_POST ||
            super.supportsInterface(interfaceId);
    }
}
